package com.syntax.class03;

public class Cake {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		
		int a=10;
		
		a+=100;
		a-=67;
		
		System.out.println(a);
		
		float cake=11;
		
		cake/=4;
		
		System.out.println(cake);
		
		
		int cake2=25;
		cake2%=7;
		
		System.out.println(cake2);
		

	}

}
